import React, { useState } from 'react'

import email from 'assets/email.png';
import mobile from 'assets/mobile.png';

const Contact = () => {

    const [ formData, setFormData ] = useState({ name: '', email: '', message: '' })
  const [ isFormSubmitted, setIsFormSubmitted ] = useState(false)
  const [ loading, setLoading ] = useState(false);

  const { name, email, message } = formData;

  const handleChangeInput = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  }


  return (
    <div>
         <h2 className='head-text'>Take a <span>Coffee</span> & <span>Chat</span> with me</h2>
      <div className='app__footer-cards'>
          <div className='app__footer-card'>
            <img src={email} alt='email'/>
            <a href='mailto:khanzaryab007@gmail.com' className='p-text'>hello@zaryab.com</a>
          </div>
          <div className='app__footer-card'>
            <img src={mobile} alt='mobile'/>
            <a href='tel:+923006052364' className='p-text'>+923006052364</a>
          </div>
      </div>
      <div className='app__footer-form app__flex'>
        <div className='app__flex'> 
          <input className='p-text' type='text' placeholder='Your Name' name='name' value={name} onChange={handleChangeInput} />
        </div>
        <div className='app__flex'>
          <input className='p-text' type='text' placeholder='Your Email' name='email' value={email} onChange={handleChangeInput} />
        </div>
        <div>
          <textarea 
            className='p-text'
            placeholder='Your Message'
            value={message}
            name='message'
            onChange={handleChangeInput}
          />
        </div>
        <button type='button' className='p-text' onClick={handleSubmit}>{loading ? 'Sending...' : 'Send Message'}</button>
      </div>
    </div>
  )
}

export default Contact;
